package com.lj.iot.common.aiui.core.vo;import com.alibaba.fastjson.JSONObject;import java.util.ArrayList;import java.util.List;/** * iflyos意图返回结果 * * @Copyright: */public final class IntentResult {    public static IntentResult MSG(String msg){        IntentResult intentResult = new IntentResult();        intentResult.getResponse().setOutputSpeech(msg);        return intentResult;    }    private final String version;    private final Response response = new Response();    public IntentResult() {        this("2.1");    }    public IntentResult(String version) {        this.version = version;    }    public String getVersion() {        return version;    }    public Response getResponse() {        return response;    }    public static final class Response {        private boolean expectSpeech = false;        private boolean shouldEndSession = false;        private PlainText outputSpeech;        private final List<Directive> directives = new ArrayList<>();        public boolean isExpectSpeech() {            return expectSpeech;        }        public void withExpectSpeech(boolean expectSpeech) {            this.expectSpeech = expectSpeech;        }        public boolean isShouldEndSession() {            return shouldEndSession;        }        public void withShouldEndSession(boolean shouldEndSession) {            this.shouldEndSession = shouldEndSession;        }        public PlainText getOutputSpeech() {            return outputSpeech;        }        public void setOutputSpeech(String text) {            this.outputSpeech = ofPlainText(text);        }        public List<Directive> getDirectives() {            return directives;        }        public void addDirective(Directive directive) {            this.directives.add(directive);        }    }    public static PlainText ofPlainText(String text) {        return new PlainText(text);    }    public static final class PlainText {        private final String type;        private final String text;        public PlainText(String type, String text) {            this.type = type;            this.text = text;        }        public PlainText(String text) {            this("PlainText", text);        }        public String getType() {            return type;        }        public String getText() {            return text;        }    }    public static final class Directive {        private final String type;        private JSONObject payload = new JSONObject();        public Directive() {            this("Custom");        }        public Directive(String type) {            this.type = type;        }        public String getType() {            return type;        }        public JSONObject getPayload() {            return payload;        }        public void setPayload(JSONObject payload) {            this.payload = payload;        }        public void addPair(String key, Object value) {            this.payload.put(key, value);        }    }}